INSERT INTO dbo.Platform (PlatformID, LanguageID, CountryID, Name, ShortDescription, LongDescription, FeesDescription, PositiveAspects, NegativeAspects, Advice, SignUpURL, SignInURL, CreatedDate, UpdatedDate, ModifiedBy, Active) VALUES (8, 1, 1, N'Upwork', N'Marketplace for business services.', 'Online workplace where savvy businesses and professional freelancers go to work', '["20% for the first $500 billed with the client.", "10% for lifetime billings with the client between $500.01 and $10,000.", "5% for lifetime billings with the client that exceed $10,000."]', '["Freedom from the constraints of a 9-to-5 office job", "Potential to meet new clients"]', '["Time invested in building a reputation may not outweigh money earned through gigs."] ', '["Build a reputation by specializing your offerings and earning high ratings."] ', N'https://www.upwork.com/i/how-it-works/freelancer/', N'https://www.upwork.com/ab/account-security/login', '2018-02-05 00:00:00.0000000 +00:00', '2018-02-05 00:00:00 +00:00', N'jd', 1);
